{"_when":"pre","fi_events_type":"database","pp":{"post_process_id":"356","post_process_when":"update","post_process_entity_id":"131","post_process_module":"products_manager","post_process_crm":"1","post_process_api":"1","post_process_apilib":"1"},"fi_events_action":"custom_code","fi_events_actiondata":{"code":"if (
    $this->module->moduleExists('payments-subscriptions')
    && array_key_exists('applica_prezzo_subscriptions', $data['post']) 
    && !empty($data['post']['applica_prezzo_subscriptions']) 
    && $data['post']['applica_prezzo_subscriptions'] == DB_BOOL_TRUE
) {
    $product_id = $data['post']['fw_products_id'];
    $product_price = $data['post']['fw_products_sell_price'];

    $subscriptions_collegate = $this->apilib->search('subscriptions', ['subscriptions_product' => $product_id, 'subscriptions_end_date > NOW()' ]);

    if (!empty($subscriptions_collegate)) {
        foreach ($subscriptions_collegate as $subscription) {
            if ($subscription['fw_products_sell_price'] === $product_price) {
                $this->apilib->edit('subscriptions', ['subscriptions_price' => $product_price]);
            }
        }
    }

    unset($data['post']['applica_prezzo_subscriptions'], $data['original_post']['applica_prezzo_subscriptions']);
}"},"fi_events_title":"Applica cambio prezzo ad eventuali subscription collegate se checkbox true","save":"0"}

